mapper.update:
UPDATE reservation
SET res_id=#{res_id}, check_in=#{check_in}, check_out=#{check_out}, res_people=#{res_people}, room_num=#{room_num}, regdate=#{regdate}
WHERE num=#{num}

mapper.select:
SELECT num, res_id , check_in , check_out , res_people , room_num , regdate
FROM reservation
WHERE num=#{num}
