mapper.select:
SELECT num, room_name, room_price, room_structure, room_size, room_people, room_items, bedroom_type, room_introduce, img_path
FROM travel
WHERE num=#{num}

mapper.update:
UPDATE room
SET num=#{num}, room_name=#{room_name}, room_price=#{room_price}, room_structure=#{room_structure}, room_size=#{room_size}, room_people=#{room_people}, room_items=#{room_items}, bedroom_type=#{bedroom_type}, room_introduce=#{room_introduce}, img_path=#{img_path}
WHERE num=#{num}
