mapper.insert:
UPDATE users
SET sessionId=#{sessionId}
WHERE id=#{id}

mapper.update:
UPDATE users
SET sessionId=''
WHERE id=#{id}

mapper.select:
SELECT id
FROM users
WHERE sessionId=#{decodedSessionId}

SELECT id ,password, email, name
FROM users
WHERE id=#{id }

mapper.update:
UPDATE users
SET password= #{newpassword }
WHERE id= #{id}

UPDATE users
SET name=#{name }, email=#{email }
WHERE id=#{id }
